mapper.insert:

insert into BOARD_STUDY(
						SEQ
						,MEM_ID
						,MEM_NAME
						,BOARD_SUBJECT
						,BOARD_CONTENT
						,REG_DATE
						,VIEW_CNT
						)
values(
	  (select nvl(max(SEQ)+1,1) from BOARD_STUDY)
	  ,#{MEM_ID}
	  ,#{MEM_NAME}
	  ,#{BOARD_SUBJECT}
	  ,#{BOARD_CONTENT}
	  ,sysdate
	  ,0			
)

mapper.select:
select
SEQ ,MEM_ID ,MEM_NAME ,BOARD_SUBJECT ,BOARD_CONTENT
FROM BOARD_STUDY WHERE SEQ = #{seq}

mapper.update:
UPDATE BOARD_STUDY 
SET 			
	MEM_ID			= #{MEM_ID}
	,MEM_NAME		= #{MEM_NAME}
	,BOARD_SUBJECT	= #{BOARD_SUBJECT}
	,BOARD_CONTENT	= #{BOARD_CONTENT}
	,UPT_DATE		= sysdate
WHERE SEQ = #{SEQ}
